import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to appear (using ID selector)
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to render (using :first-child strategy)
    const firstMessageSelector = '#messages-list > div:first-child';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Hover over the first message to reveal actions/reactions (if needed)
    await page.hover(firstMessageSelector);

    // Wait a short moment for potential reaction button to appear
    await page.waitForTimeout(500);

    // Try multiple selector strategies for the thumbs up reaction:

    // Strategy 1: Look for a visible reaction button inside the first message (by aria-label or icon)
    let reactionButton = await page.$(`${firstMessageSelector} button[aria-label*="thumb"], ${firstMessageSelector} .fa-thumbs-up, ${firstMessageSelector} [data-reaction="thumbs-up"]`);

    // Strategy 2: Fallback - look for a button with 'ðŸ‘' text or title inside the message
    if (!reactionButton) {
      reactionButton = await page.$(`${firstMessageSelector} button:has-text("ðŸ‘")`);
    }

    // Strategy 3: Fallback - search for any button with title including "Thumb" or "Like"
    if (!reactionButton) {
      reactionButton = await page.$(`${firstMessageSelector} button[title*="Thumb"], ${firstMessageSelector} button[title*="Like"]`);
    }

    // Strategy 4: Fallback - use xpath to find a thumbs up icon inside the first message
    if (!reactionButton) {
      const reactionHandles = await page.$x(`//div[@id="messages-list"]/div[1]//i[contains(@class, "fa-thumbs-up")]`);
      if (reactionHandles.length > 0) {
        reactionButton = reactionHandles[0];
      }
    }

    // If reaction button not found, try to reveal possible reaction bar
    if (!reactionButton) {
      // Try clicking or hovering a 'more'/'add reaction' button if present
      const addReactionBtn = await page.$(`${firstMessageSelector} button[aria-label*="Add Reaction"], ${firstMessageSelector} .fa-plus`);
      if (addReactionBtn) {
        await addReactionBtn.click();
        await page.waitForTimeout(500);
        // Try again to find thumbs up
        reactionButton = await page.$('button[aria-label*="thumb"], .fa-thumbs-up, [data-reaction="thumbs-up"]');
        if (!reactionButton) {
          reactionButton = await page.$('button:has-text("ðŸ‘")');
        }
      }
    }

    // Click the thumbs up reaction if found
    if (reactionButton) {
      await reactionButton.click();
      await page.waitForTimeout(700);

      // Take screenshot after reaction is added
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));
    } else {
      // If not found, take a screenshot for debugging
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_no_reaction_button_found.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_no_reaction_button_found.png'));
      return {
        success: false,
        action: "Could not find a thumbs up reaction button to add to the first message",
        extractedData: {},
        screenshots,
        error: "Thumbs up reaction button not found"
      };
    }

    // Extract data: verify that a reaction was added (look for 'thumbs up' icon or count under the first message)
    let thumbsUpCount = 0;
    // Try to find a thumbs up reaction count element
    const reactionCountEl = await page.$(`${firstMessageSelector} .fa-thumbs-up + span, ${firstMessageSelector} [data-reaction="thumbs-up"] + span`);

    if (reactionCountEl) {
      const countText = await reactionCountEl.innerText();
      thumbsUpCount = parseInt(countText, 10) || 1; // fallback to 1 if not a number
    } else {
      // As fallback, check for presence of thumbs up icon under the first message
      const thumbsUpIcon = await page.$(`${firstMessageSelector} .fa-thumbs-up, ${firstMessageSelector} [data-reaction="thumbs-up"]`);
      if (thumbsUpIcon) {
        thumbsUpCount = 1;
      }
    }

    return {
      success: true,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData: {
        thumbsUpOnFirstMessage: thumbsUpCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

