import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user info section to be visible (where the settings gear is)
    await page.waitForSelector('.user-info', { timeout: 5000 });

    // Find the settings gear icon inside user-controls
    // There are three icons: microphone, headphones, cog (settings)
    // The cog is the last .fas.fa-cog inside .user-controls
    const [settingsIcon] = await page.$$('.user-controls .fa-cog');
    if (!settingsIcon) {
      throw new Error('Settings gear icon not found in user-controls');
    }

    // Scroll into view and click the settings gear icon
    await settingsIcon.scrollIntoViewIfNeeded();
    await settingsIcon.click();

    // Wait for the user settings modal/dialog to appear
    // Try to detect a modal or overlay that appears after clicking settings
    // Since we don't have the modal HTML, try to wait for a generic modal/dialog
    // Try common selectors: .modal, .user-settings, .settings-modal, or a dialog with role="dialog"
    let modalSelector = null;
    const possibleSelectors = [
      '.modal',
      '.user-settings',
      '.settings-modal',
      '[role="dialog"]',
      '.settings-dialog',
      '.modal-overlay'
    ];
    for (const sel of possibleSelectors) {
      const modal = await page.$(sel);
      if (modal) {
        modalSelector = sel;
        break;
      }
    }

    // If not found, wait for any new element after click
    if (!modalSelector) {
      // Wait for a new element to appear in the DOM
      // Use a timeout to avoid hanging
      await page.waitForTimeout(1000);
      // Try again
      for (const sel of possibleSelectors) {
        const modal = await page.$(sel);
        if (modal) {
          modalSelector = sel;
          break;
        }
      }
    }

    // Take screenshot after clicking settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_clicked.png'));

    // Extract data: check if modal/dialog is visible
    let modalVisible = false;
    if (modalSelector) {
      const modalHandle = await page.$(modalSelector);
      if (modalHandle) {
        const visible = await modalHandle.evaluate(el => {
          const style = window.getComputedStyle(el);
          return style && style.display !== 'none' && style.visibility !== 'hidden' && el.offsetParent !== null;
        });
        modalVisible = visible;
      }
    }

    return {
      success: modalVisible,
      action: "Clicked the settings gear icon to open user settings modal",
      extractedData: {
        userSettingsModalVisible: modalVisible,
        modalSelector: modalSelector || null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for the user info section to be visible (bottom of channel-sidebar)
    await page.waitForSelector('.channel-sidebar .user-info', { timeout: 5000 });

    // Wait for the settings "gear" icon (FontAwesome cog) in the user-controls
    // Try to locate by xpath: the last <i> with class 'fa-cog' inside '.user-info .user-controls'
    const cogSelector = '.channel-sidebar .user-info .user-controls .fa-cog';
    await page.waitForSelector(cogSelector, { timeout: 5000 });

    // Take a screenshot before clicking the settings
    const step2 = path.join(screenshotsDir, 'step_2_user_info_visible.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Click the settings gear icon
    const cogElement = await page.$(cogSelector);
    if (!cogElement) throw new Error('Settings gear icon not found');
    await cogElement.click();

    // Wait for the user settings modal/dialog to appear
    // Try several strategies: look for modal by role, aria, or class/id (since HTML isn't fully shown)
    // Common: look for a modal with class like '.modal', '.user-settings', or a dialog
    // We'll look for a modal-ish div that appears after clicking
    let modalSelector = '.user-settings-modal, .modal, .settings-modal, [role="dialog"]';
    let foundModal = null;
    try {
      foundModal = await page.waitForSelector(modalSelector, { timeout: 3000 });
    } catch {
      // Try a more generic strategy: look for a visible modal/dialog
      const allDialogs = await page.$$('div[role="dialog"], div[class*=modal], div[class*=settings]');
      for (const d of allDialogs) {
        if (await d.isVisible()) {
          foundModal = d;
          break;
        }
      }
    }

    // Take screenshot after clicking gear icon (whether or not modal appeared)
    const step3 = path.join(screenshotsDir, 'step_3_after_gear_click.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract some data to confirm modal opened: get modal title or presence
    let extractedData = {};
    if (foundModal) {
      // Try to get the modal's title text
      const modalTitle = await foundModal.$eval('h2, h1, .modal-title', el => el.textContent.trim()).catch(() => null);
      extractedData = {
        modalVisible: true,
        modalTitle: modalTitle || null
      };
    } else {
      extractedData = {
        modalVisible: false
      };
    }

    return {
      success: extractedData.modalVisible === true,
      action: "Clicked the user settings gear icon to open user settings modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for the user controls section in the channel sidebar
    // Use a robust selector: .user-controls is a DIV with three <i> icons, the last is the settings (gear)
    await page.waitForSelector('.user-controls', { timeout: 5000 });

    // Locate the settings gear icon inside .user-controls
    // We use an XPath selector for the <i> with class 'fa-cog' within .user-controls
    const gearIcon = await page.waitForSelector('//div[contains(@class,"user-controls")]//i[contains(@class,"fa-cog")]', { timeout: 3000 });

    // Scroll into view to ensure clickability
    await gearIcon.scrollIntoViewIfNeeded();

    // Take screenshot before clicking the settings
    const step2 = path.join(screenshotsDir, 'step_2_before_settings_click.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Click the settings gear icon
    await gearIcon.click();

    // Wait for the user settings modal to appear
    // Try different strategies: look for a modal with "User Settings" text, or a modal/dialog with a close ("×") button
    let settingsModal;
    try {
      // Try to find by heading/text
      settingsModal = await page.waitForSelector('//div[contains(@class,"modal") or contains(@class,"user-settings") or contains(@class,"settings")]//*[contains(text(),"User Settings") or contains(text(),"Settings")]', { timeout: 2500 });
    } catch {}

    if (!settingsModal) {
      // Fallback: try finding a modal/dialog with a close "×" button
      settingsModal = await page.waitForSelector('//div[contains(@class,"modal") or contains(@class,"user-settings") or contains(@class,"settings")]//button[contains(text(),"×")]', { timeout: 2500 });
    }

    // Wait a little for any animations
    await page.waitForTimeout(400);

    // Take screenshot after settings modal is (hopefully) open
    const step3 = path.join(screenshotsDir, 'step_3_user_settings_opened.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract info to verify: check if a modal/dialog with "Settings" is visible
    let modalTitle = null;
    if (settingsModal) {
      // Try to extract visible text from the modal
      modalTitle = await settingsModal.evaluate(el => el.textContent);
    } else {
      // Fallback: try to find any element with "Settings" visible on the page
      const anySettingsElem = await page.$('text=/Settings/i');
      modalTitle = anySettingsElem ? await anySettingsElem.evaluate(el => el.textContent) : null;
    }

    return {
      success: !!settingsModal || !!modalTitle,
      action: "Clicked user settings gear icon and attempted to open user settings modal",
      extractedData: {
        modalTitle: modalTitle ? modalTitle.trim() : null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
