import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for messages-list container to load
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear (using descendant .messages-list > div or similar)
    // Use xpath to be robust: find the first message block
    // The script.js suggests messages are rendered as direct children of #messages-list.
    const firstMessageSelector = '#messages-list > div';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Hover over the first message to attempt to reveal any reaction buttons (simulate Discord UI)
    const firstMessage = await page.$(firstMessageSelector);
    if (!firstMessage) throw new Error("First message not found");

    await firstMessage.hover();
    // Wait a moment for any hover-activated controls to appear
    await page.waitForTimeout(500);

    // Try to find a reaction/add-reaction button within the first message
    // Try several strategies:
    // 1. By aria-label or title
    // 2. By emoji icon
    // 3. By button with plus, smiley, or similar
    // We'll attempt to find a button with a '+', 'ðŸ˜Š', 'ðŸ˜€', or 'Add Reaction'

    // Try multiple selectors in order
    let reactionButton = null;
    const possibleSelectors = [
      'button[aria-label="Add Reaction"]',
      'button[title="Add Reaction"]',
      'button:has-text("+")',
      'button:has-text("ðŸ˜Š")',
      'button:has-text("ðŸ˜€")',
      'button:has-text("ðŸ‘")',
      'button.add-reaction',
      'button.reaction-button',
      'button' // fallback to any button inside message
    ];

    for (const sel of possibleSelectors) {
      reactionButton = await firstMessage.$(sel);
      if (reactionButton) break;
    }

    // If not found, try to find a button or clickable element with "Add Reaction" as text
    if (!reactionButton) {
      const buttons = await firstMessage.$$('button, [role="button"], .add-reaction');
      for (const btn of buttons) {
        const txt = (await btn.innerText()).toLowerCase();
        if (
          txt.includes('reaction') ||
          txt.includes('thumb') ||
          txt.includes('ðŸ‘') ||
          txt.includes('add') ||
          txt.includes('+')
        ) {
          reactionButton = btn;
          break;
        }
      }
    }

    if (!reactionButton) {
      // As a last resort, click the first button in the message
      const fallbackButton = await firstMessage.$('button');
      if (fallbackButton) {
        reactionButton = fallbackButton;
      }
    }

    if (!reactionButton) throw new Error("Unable to locate 'Add Reaction' button on first message");

    // Click the reaction button to open the reaction picker (if any)
    await reactionButton.click();
    await page.waitForTimeout(500);

    // Now try to select the thumbs up emoji ("ðŸ‘")
    // Try several selector strategies inside the picker:
    // 1. Button or span with text "ðŸ‘"
    // 2. aria-label or title "Thumbs Up" or similar

    // Search in the document (in case the picker is rendered elsewhere)
    let thumbsUpSelector = [
      'button:has-text("ðŸ‘")',
      'span:has-text("ðŸ‘")',
      '[aria-label*="thumbs up"], [aria-label*="Thumbs Up"]',
      '[title*="thumbs up"], [title*="Thumbs Up"]',
      '.emoji-picker .emoji:has-text("ðŸ‘")',
      '.emoji:has-text("ðŸ‘")'
    ];
    let thumbsUp = null;
    for (const sel of thumbsUpSelector) {
      thumbsUp = await page.$(sel);
      if (thumbsUp) break;
    }

    // As fallback, look for any element with "ðŸ‘"
    if (!thumbsUp) {
      const allEmoji = await page.$$('button, span, div');
      for (const e of allEmoji) {
        const txt = await e.innerText();
        if (txt && txt.includes('ðŸ‘')) {
          thumbsUp = e;
          break;
        }
      }
    }

    if (!thumbsUp) throw new Error("Unable to locate 'Thumbs Up' emoji in reaction picker");

    // Click the thumbs up emoji to react
    await thumbsUp.click();
    await page.waitForTimeout(800);

    // Take screenshot after reaction
    const step2 = path.join(screenshotsDir, 'step_2_reacted.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Verify that thumbs up reaction is present on the first message
    // Look for 'ðŸ‘' emoji rendered within the first message
    const reactionPresent = await firstMessage.$(':text("ðŸ‘")');
    let reactionCount = 0;
    if (reactionPresent) {
      // Optionally get count if present
      const parent = await reactionPresent.evaluateHandle(node => node.parentElement);
      const countNode = await parent.$('span');
      if (countNode) {
        const txt = await countNode.innerText();
        reactionCount = parseInt(txt, 10) || 1;
      } else {
        reactionCount = 1;
      }
    }

    return {
      success: !!reactionPresent,
      action: "Added thumbs up reaction to the first message in the channel",
      extractedData: {
        thumbsUpPresent: !!reactionPresent,
        thumbsUpCount: reactionCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
