import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for messages to be loaded in the messages list
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear
    // Try to select the first message container (assume .message or similar class)
    let messageSelector;
    // Try common selectors for a message
    const possibleSelectors = [
      '.message', // typical
      '.message-item',
      '.messages-list > div', // fallback: direct children of messages-list
      '[class*=message]' // partial match
    ];
    for (const sel of possibleSelectors) {
      const el = await page.$(`${sel}`);
      if (el) {
        messageSelector = sel;
        break;
      }
    }
    if (!messageSelector) {
      throw new Error('Could not find a message element in the messages list');
    }

    // Hover over the first message to reveal the reaction/add-reaction button
    const firstMessage = (await page.$$(messageSelector))[0];
    if (!firstMessage) {
      throw new Error('No messages found to react to');
    }
    await firstMessage.hover();
    // Wait a bit for hover effects
    await page.waitForTimeout(400);

    // Try to find the add reaction button (commonly a smiley face, plus, or emoji icon)
    // Try several selectors/strategies
    let addReactionButton = null;
    const addReactionSelectors = [
      'button[aria-label*="Add Reaction"]',
      'button[title*="Add Reaction"]',
      'button[aria-label*="reaction"]',
      'button[title*="reaction"]',
      'button i.fas.fa-smile', // font-awesome smile icon
      'button i.fa-smile',
      'button i.fa-plus',
      'button i.fas.fa-plus',
      'button', // fallback: any button inside the message
      '[class*=reaction]',
      '[class*=emoji]',
      '[class*=add]'
    ];
    for (const sel of addReactionSelectors) {
      // Try to find within the message only
      addReactionButton = await firstMessage.$(sel);
      if (addReactionButton) break;
    }
    // If not found, try globally (sometimes reaction bar is outside message)
    if (!addReactionButton) {
      for (const sel of addReactionSelectors) {
        addReactionButton = await page.$(sel);
        if (addReactionButton) break;
      }
    }
    if (!addReactionButton) {
      throw new Error('Could not find the add reaction button on the first message');
    }

    // Scroll into view and click the add reaction button
    await addReactionButton.scrollIntoViewIfNeeded();
    await addReactionButton.click();

    // Wait for the emoji picker to appear
    // Try to detect a popup, modal, or emoji grid
    let emojiPickerSelector = null;
    const emojiPickerSelectors = [
      '[class*=emoji-picker]',
      '[class*=emoji][class*=popup]',
      '[class*=emoji][class*=container]',
      '[class*=emoji][class*=list]',
      '[class*=emoji][class*=panel]',
      '[role="dialog"]',
      '[role="menu"]',
      '.emoji-picker',
      '.emoji-list',
      '.emoji-panel'
    ];
    for (const sel of emojiPickerSelectors) {
      const found = await page.$(sel);
      if (found) {
        emojiPickerSelector = sel;
        break;
      }
    }
    // Wait up to 2s for the emoji picker to appear
    if (!emojiPickerSelector) {
      for (const sel of emojiPickerSelectors) {
        try {
          await page.waitForSelector(sel, { timeout: 2000 });
          emojiPickerSelector = sel;
          break;
        } catch (e) {}
      }
    }
    if (!emojiPickerSelector) {
      throw new Error('Emoji picker did not appear after clicking add reaction');
    }

    // Take screenshot after emoji picker is open
    const step2 = path.join(screenshotsDir, 'step_2_emoji_picker_open.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Extract some data: check if emoji picker is visible
    const emojiPickerVisible = await page.$eval(emojiPickerSelector, el => {
      const style = window.getComputedStyle(el);
      return style && style.display !== 'none' && style.visibility !== 'hidden' && el.offsetParent !== null;
    });

    return {
      success: true,
      action: "Opened emoji picker for message reaction by clicking the add reaction button on a message.",
      extractedData: {
        emojiPickerVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

