export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Wait for messages area to load by its id
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear
    // We'll use the structure from the code: messages will be rendered dynamically.
    // Wait for a message author avatar as a sign of message ready
    await page.waitForSelector('.messages-list .message .message-avatar img, .messages-list .message', { timeout: 5000 });

    // Hover over the first message to reveal possible action buttons (like add reaction)
    // Use nth-child and fallback to first .message element
    const messageSelector = '.messages-list .message';
    await page.waitForSelector(messageSelector);
    const firstMessage = await page.$(messageSelector);

    // Hover to ensure any hidden controls are displayed
    await firstMessage.hover();

    // Give UI a moment to show reaction/add emoji icon if it appears on hover
    await page.waitForTimeout(300);

    // Try to find an add-reaction button/icon next to the first message
    // Since the HTML is not fully shown, use common patterns for Discord clones
    // Try button with emoji or smiley icon, or .fa-smile, .fa-face-smile, .fa-plus, or a button next to message
    let reactionButton = null;

    // Try various selectors
    const reactionButtonSelectors = [
      // Font Awesome smiley or plus icons
      '.message-actions .fa-smile',
      '.message-actions .fa-face-smile',
      '.message-actions .fa-plus',
      // Button with emoji label or aria-label
      '.message-actions button[aria-label*="Add Reaction"]',
      '.message-actions button[title*="Add Reaction"]',
      '.message-actions button',
      // Fallback: any button or icon inside message
      '.message button',
      '.message .fa-plus',
      '.message .fa-smile',
      '.message .fa-face-smile',
      // Use XPath: emoji or add reaction by inner text or title
      '//button[contains(@title, "Add Reaction")]',
      '//button[contains(@aria-label, "Add Reaction")]',
      '//i[contains(@class, "fa-smile")]',
      '//i[contains(@class, "fa-face-smile")]',
      '//i[contains(@class, "fa-plus")]'
    ];

    for (const selector of reactionButtonSelectors) {
      if (selector.startsWith('//')) {
        reactionButton = await page.$(selector);
      } else {
        reactionButton = await firstMessage.$(selector) || await page.$(selector);
      }
      if (reactionButton) break;
    }

    if (!reactionButton) {
      // Try to reveal action buttons by hovering over the message again and retry
      await firstMessage.hover();
      await page.waitForTimeout(300);
      for (const selector of reactionButtonSelectors) {
        if (selector.startsWith('//')) {
          reactionButton = await page.$(selector);
        } else {
          reactionButton = await firstMessage.$(selector) || await page.$(selector);
        }
        if (reactionButton) break;
      }
    }

    if (!reactionButton) {
      throw new Error('Could not locate the add reaction button for the first message.');
    }

    // Click the add reaction button to open emoji picker
    await reactionButton.click();

    // Wait for emoji picker to appear - look for a container with emoji, or input for emoji search
    // Try various selectors
    const emojiPickerSelectors = [
      '.emoji-picker',
      '.emoji-menu',
      '.emoji-container',
      '.emoji-picker__container',
      '.emoji-picker__search',
      '.emoji-picker input[type="text"]',
      '[placeholder*="emoji"]',
      '[aria-label*="emoji"]',
      // Fallback: look for emoji grid
      '.emoji-grid',
      '.emoji-list',
      // XPath: element with emoji in class or aria-label
      '//*[contains(@class,"emoji") and (contains(@class,"picker") or contains(@class,"container"))]',
      '//*[contains(@aria-label,"emoji picker")]'
    ];

    let emojiPickerAppeared = false;
    for (const selector of emojiPickerSelectors) {
      try {
        if (selector.startsWith('//')) {
          await page.waitForXPath(selector, { timeout: 2000 });
        } else {
          await page.waitForSelector(selector, { timeout: 2000 });
        }
        emojiPickerAppeared = true;
        break;
      } catch (e) {
        // continue trying next selector
      }
    }

    // Take screenshot after attempting to open emoji picker
    const step2 = path.join(screenshotsDir, 'step_2_emoji_picker_open_attempt.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Extract result data for verification
    let extractedData = {};
    if (emojiPickerAppeared) {
      extractedData = { emoji_picker_opened: true, message_reactions: true };
    } else {
      extractedData = { emoji_picker_opened: false, message_reactions: false };
    }

    return {
      success: emojiPickerAppeared,
      action: "Attempted to open emoji picker for message reaction using alternative selectors and strategies.",
      extractedData,
      screenshots,
      error: emojiPickerAppeared ? null : "Emoji picker did not appear after clicking the add reaction button."
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
