import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to be loaded
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear
    // The messages are likely rendered dynamically, so wait for a message element
    // We'll try to find a message by looking for an element with an avatar and message content
    // Try a generic selector for a message container
    const firstMessageSelector = '#messages-list > div, #messages-list .message, #messages-list .message-item';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Find the first message element
    const firstMessageHandle = await page.$(firstMessageSelector);

    if (!firstMessageHandle) {
      throw new Error('First message not found');
    }

    // Hover over the first message to reveal the reaction button (if required)
    await firstMessageHandle.hover();
    await page.waitForTimeout(300); // Give time for any hover UI

    // Try to find the reaction button (thumbs up)
    // Try several possible selectors for a reaction/emoji button
    let reactionButtonHandle = null;
    // Try to find a button or element inside the message that could be the reaction trigger
    const possibleReactionSelectors = [
      'button[aria-label*="Add Reaction"]',
      '.add-reaction',
      '.reaction-button',
      'button[title*="Add Reaction"]',
      'button[aria-label*="emoji"]',
      '.fa-smile', // FontAwesome icon for emoji
      '.fa-thumbs-up', // FontAwesome icon for thumbs up
      'button', // fallback
      '[role="button"]'
    ];

    for (const sel of possibleReactionSelectors) {
      reactionButtonHandle = await firstMessageHandle.$(sel);
      if (reactionButtonHandle) break;
    }

    // If not found, try to click at a likely position (simulate emoji button at end of message)
    if (!reactionButtonHandle) {
      // Try to find any button in the first message
      const buttons = await firstMessageHandle.$$('button');
      if (buttons.length > 0) {
        reactionButtonHandle = buttons[buttons.length - 1]; // Try the last button
      }
    }

    if (!reactionButtonHandle) {
      // As a last resort, try to click at the end of the message to trigger emoji popup
      const boundingBox = await firstMessageHandle.boundingBox();
      if (boundingBox) {
        await page.mouse.click(
          boundingBox.x + boundingBox.width - 10,
          boundingBox.y + boundingBox.height / 2
        );
        await page.waitForTimeout(300);
      }
    } else {
      // Click the reaction/emoji button to open the emoji picker
      await reactionButtonHandle.click();
      await page.waitForTimeout(400);
    }

    // Wait for emoji picker to appear
    // Try several selectors for emoji picker or emoji list
    const emojiPickerSelectors = [
      '.emoji-picker',
      '.emoji-list',
      '.emoji-menu',
      '[role="menu"]',
      '[class*="emoji"]'
    ];
    let emojiPickerFound = false;
    let emojiPickerSelectorUsed = null;
    for (const sel of emojiPickerSelectors) {
      if (await page.$(sel)) {
        emojiPickerFound = true;
        emojiPickerSelectorUsed = sel;
        break;
      }
    }
    // If not found, just proceed (maybe emojis are inline)

    // Try to select the thumbs up emoji
    // Try to find an emoji with aria-label or title containing "thumbs up" or "like"
    const thumbsUpSelectors = [
      '[aria-label*="thumbs up"]',
      '[title*="thumbs up"]',
      '[aria-label*="like"]',
      '[title*="like"]',
      '.fa-thumbs-up',
      'img[alt="ðŸ‘"]',
      'span:has-text("ðŸ‘")',
      'button:has-text("ðŸ‘")'
    ];
    let thumbsUpHandle = null;
    for (const sel of thumbsUpSelectors) {
      thumbsUpHandle = await page.$(sel);
      if (thumbsUpHandle) break;
    }

    // If not found, try to type "thumb" in the emoji search input if available
    if (!thumbsUpHandle) {
      // Try to find emoji search input
      const emojiSearchSelectors = [
        'input[type="text"][placeholder*="Search"]',
        'input[aria-label*="Search"]',
        '.emoji-search input'
      ];
      let searchInput = null;
      for (const sel of emojiSearchSelectors) {
        searchInput = await page.$(sel);
        if (searchInput) break;
      }
      if (searchInput) {
        await searchInput.fill('thumb');
        await page.waitForTimeout(400);
        // Try again to find the thumbs up emoji
        for (const sel of thumbsUpSelectors) {
          thumbsUpHandle = await page.$(sel);
          if (thumbsUpHandle) break;
        }
      }
    }

    // If still not found, try to click the first emoji in the picker
    if (!thumbsUpHandle && emojiPickerFound) {
      const firstEmoji = await page.$(`${emojiPickerSelectorUsed} button, ${emojiPickerSelectorUsed} img, ${emojiPickerSelectorUsed} span`);
      if (firstEmoji) {
        thumbsUpHandle = firstEmoji;
      }
    }

    if (!thumbsUpHandle) {
      throw new Error('Thumbs up emoji not found in emoji picker');
    }

    // Click the thumbs up emoji to react
    await thumbsUpHandle.click();
    await page.waitForTimeout(600);

    // Take a screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    // Verify that the thumbs up reaction is now visible on the first message
    // Look for a thumbs up emoji or reaction count on the first message
    const thumbsUpReactionSelectors = [
      '.fa-thumbs-up',
      'img[alt="ðŸ‘"]',
      'span:has-text("ðŸ‘")',
      'button:has-text("ðŸ‘")',
      '[aria-label*="thumbs up"]',
      '[title*="thumbs up"]'
    ];
    let reactionFound = false;
    for (const sel of thumbsUpReactionSelectors) {
      const reaction = await firstMessageHandle.$(sel);
      if (reaction) {
        reactionFound = true;
        break;
      }
    }

    // Extract the list of reactions on the first message for reporting
    const reactions = await firstMessageHandle.$$eval(
      'button, span, img',
      nodes => nodes
        .filter(n =>
          (n.alt && n.alt.includes('ðŸ‘')) ||
          (n.innerText && n.innerText.includes('ðŸ‘')) ||
          (n.getAttribute && n.getAttribute('aria-label') && n.getAttribute('aria-label').toLowerCase().includes('thumbs up')) ||
          (n.getAttribute && n.getAttribute('title') && n.getAttribute('title').toLowerCase().includes('thumbs up'))
        )
        .map(n => n.alt || n.innerText || n.getAttribute('aria-label') || n.getAttribute('title'))
    );

    return {
      success: reactionFound,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData: {
        reactions
      },
      screenshots,
      error: reactionFound ? null : "Thumbs up reaction not visible after adding"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

