import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for messages list to be visible
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to appear in the messages list
    // Try to select the first message by a likely selector (div.message, or direct child of #messages-list)
    let firstMessageSelector;
    // Try common selectors
    const possibleSelectors = [
      '#messages-list .message',
      '#messages-list > div',
      "//div[@id='messages-list']/div"
    ];
    let firstMessageHandle = null;
    for (const sel of possibleSelectors) {
      try {
        if (sel.startsWith('//')) {
          const handles = await page.$x(sel);
          if (handles.length > 0) {
            firstMessageHandle = handles[0];
            firstMessageSelector = sel;
            break;
          }
        } else {
          const handle = await page.$(sel);
          if (handle) {
            firstMessageHandle = handle;
            firstMessageSelector = sel;
            break;
          }
        }
      } catch (e) {}
    }
    if (!firstMessageHandle) {
      throw new Error('Could not find the first message in the messages list');
    }

    // Hover over the first message to reveal the reaction button (if needed)
    await firstMessageHandle.hover();
    await page.waitForTimeout(500); // Wait for any hover effects

    // Try to find the "Add Reaction" button within the first message
    // Commonly, this is a button with emoji or a plus icon
    let addReactionButton = null;
    // Try several possible selectors inside the first message
    const addReactionSelectors = [
      'button[aria-label*="Add Reaction"]',
      'button[title*="Add Reaction"]',
      'button[aria-label*="reaction"]',
      'button[title*="reaction"]',
      'button',
      '.add-reaction',
      '.reaction-button',
      'span[role="button"]',
      'i.fa-plus',
      'i.fas.fa-plus'
    ];
    for (const sel of addReactionSelectors) {
      try {
        let handle;
        if (sel.startsWith('i.') || sel.startsWith('.')) {
          handle = await firstMessageHandle.$(sel);
        } else {
          handle = await firstMessageHandle.$(sel);
        }
        if (handle) {
          addReactionButton = handle;
          break;
        }
      } catch (e) {}
    }

    // If not found, try to click at a likely position (simulate reaction button at the top-right of the message)
    if (!addReactionButton) {
      // Try to click at the top-right corner of the message
      const box = await firstMessageHandle.boundingBox();
      if (box) {
        await page.mouse.move(box.x + box.width - 10, box.y + 10);
        await page.mouse.click(box.x + box.width - 10, box.y + 10);
        await page.waitForTimeout(500);
      }
    } else {
      await addReactionButton.click();
      await page.waitForTimeout(500);
    }

    // Wait for the emoji picker to appear
    // Try to find a thumbs up emoji (üëç) in the emoji picker
    // Try several selectors for emoji picker
    let emojiButton = null;
    const emojiSelectors = [
      'button[aria-label*="thumbs up"]',
      'button[title*="thumbs up"]',
      'span[role="button"][aria-label*="thumbs up"]',
      'span[role="button"]:has-text("üëç")',
      'button:has-text("üëç")',
      'span:has-text("üëç")'
    ];
    for (const sel of emojiSelectors) {
      try {
        let handle;
        if (sel.startsWith('span:has-text') || sel.startsWith('button:has-text')) {
          handle = await page.locator(sel).first();
          if (await handle.count() > 0) {
            emojiButton = handle;
            break;
          }
        } else {
          handle = await page.$(sel);
          if (handle) {
            emojiButton = handle;
            break;
          }
        }
      } catch (e) {}
    }
    // If not found, try to click the first emoji in the picker
    if (!emojiButton) {
      // Wait for any emoji picker to appear
      await page.waitForTimeout(500);
      // Try to select the first emoji button
      const emojiPickerSelectors = [
        '.emoji-picker button',
        '.emoji-picker span[role="button"]',
        '.emoji-picker .emoji',
        'button.emoji',
        'span.emoji'
      ];
      for (const sel of emojiPickerSelectors) {
        try {
          const handle = await page.$(sel);
          if (handle) {
            emojiButton = handle;
            break;
          }
        } catch (e) {}
      }
    }

    // If still not found, try to click any visible "üëç" emoji on the page
    if (!emojiButton) {
      const thumbsUpHandles = await page.$$('span,button');
      for (const handle of thumbsUpHandles) {
        const text = await page.evaluate(el => el.textContent, handle);
        if (text && text.includes('üëç')) {
          emojiButton = handle;
          break;
        }
      }
    }

    if (!emojiButton) {
      throw new Error('Could not find thumbs up emoji in the emoji picker');
    }

    // Click the thumbs up emoji to add the reaction
    await emojiButton.click();
    await page.waitForTimeout(1000);

    // Take a screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    // Extract data: check if the first message now contains a thumbs up reaction
    // Look for a "üëç" emoji or reaction count inside the first message
    let reactionFound = false;
    let reactionCount = null;
    const possibleReactionSelectors = [
      '.reaction',
      '.reactions',
      'span',
      'button'
    ];
    for (const sel of possibleReactionSelectors) {
      try {
        const handles = await firstMessageHandle.$$(sel);
        for (const handle of handles) {
          const text = await page.evaluate(el => el.textContent, handle);
          if (text && text.includes('üëç')) {
            reactionFound = true;
            // Try to extract count if present (e.g., "üëç 1")
            const match = text.match(/üëç\s*(\d*)/);
            if (match && match[1]) {
              reactionCount = match[1];
            }
            break;
          }
        }
        if (reactionFound) break;
      } catch (e) {}
    }

    return {
      success: reactionFound,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData: {
        thumbsUpReactionPresent: reactionFound,
        thumbsUpReactionCount: reactionCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

