import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the messages list to appear using ID selector
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for the first message to render (using :nth-child(1))
    const firstMessageSelector = '#messages-list > div:nth-child(1)';
    await page.waitForSelector(firstMessageSelector, { timeout: 5000 });

    // Hover over the first message to possibly reveal reaction controls
    await page.hover(firstMessageSelector);

    // Wait for any reaction button/icon to appear (try by class and aria-label)
    // Try to find a button/icon with a tooltip or aria-label containing 'Add Reaction'
    let addReactionButton = await page.$(`${firstMessageSelector} [aria-label*="reaction"], ${firstMessageSelector} .add-reaction, ${firstMessageSelector} [title*="Reaction"], ${firstMessageSelector} [data-tooltip*="reaction"]`);
    if (!addReactionButton) {
      // Try a generic emoji/smile icon if present
      addReactionButton = await page.$(`${firstMessageSelector} i.fas.fa-smile, ${firstMessageSelector} i.fa-smile`);
    }
    if (!addReactionButton) {
      // Try to find a button with "+" inside the message as a last resort
      addReactionButton = await page.$(`${firstMessageSelector} button:has-text("+")`);
    }

    if (!addReactionButton) {
      throw new Error('Add Reaction button not found on the first message');
    }

    // Click the add reaction button
    await addReactionButton.click();
    await page.waitForTimeout(400); // Wait for emoji/reaction picker to appear

    // Try to select the thumbs up emoji from the reaction picker
    // Try several strategies for the thumbs up: aria-label, title, unicode, or text content (üëç)
    let thumbsUpSelectorCandidates = [
      '[aria-label*="thumb"], [title*="thumb"], [aria-label*="üëç"], [title*="üëç"]',
      'button:has-text("üëç")',
      '.emoji-picker [data-emoji="üëç"]',
      '.emoji-picker [title*="thumb"]',
      '.emoji-picker [aria-label*="thumb"]',
      '.emoji-picker .emoji:has-text("üëç")'
    ];
    let thumbsUpEmoji = null;
    for (const sel of thumbsUpSelectorCandidates) {
      thumbsUpEmoji = await page.$(sel);
      if (thumbsUpEmoji) break;
    }

    // If not found, try to get any emoji as fallback
    if (!thumbsUpEmoji) {
      // Try to get the first emoji from the picker
      thumbsUpEmoji = await page.$('.emoji-picker .emoji, .emoji-picker button, .emoji-picker span');
    }

    if (!thumbsUpEmoji) {
      throw new Error('Thumbs Up emoji not found in the reaction picker');
    }

    // Click the thumbs up emoji
    await thumbsUpEmoji.click();
    await page.waitForTimeout(500); // Wait for reaction to register

    // Take screenshot after adding reaction
    const afterReactionShot = path.join(screenshotsDir, 'step_2_after_reaction.png');
    await page.screenshot({ path: afterReactionShot });
    screenshots.push(afterReactionShot);

    // Extract data: check if thumbs up emoji now appears in the first message's reactions
    // Try to find a reaction element within the first message that contains a thumbs up
    let reactionThumbsUp = await page.$(`${firstMessageSelector} [aria-label*="üëç"], ${firstMessageSelector} [title*="üëç"], ${firstMessageSelector} .reaction:has-text("üëç")`);
    let thumbsUpExists = false;
    if (reactionThumbsUp) {
      thumbsUpExists = true;
    } else {
      // As fallback, check if any reaction emoji appears in the first message
      const reactionElement = await page.$(`${firstMessageSelector} .reaction, ${firstMessageSelector} [class*="reaction"]`);
      if (reactionElement) {
        const text = await reactionElement.textContent();
        if (text && text.includes('üëç')) thumbsUpExists = true;
      }
    }

    return {
      success: thumbsUpExists,
      action: "Added thumbs up reaction to the first message in the current channel",
      extractedData: {
        thumbsUpAdded: thumbsUpExists
      },
      screenshots,
      error: thumbsUpExists ? null : "Could not confirm thumbs up reaction was added"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
