import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Wait for the mobile menu toggle button to be visible
    await page.waitForSelector('#mobile-menu-toggle', { state: 'visible', timeout: 5000 });

    // Take a screenshot before clicking the mobile menu
    const beforeClickScreenshot = path.join(screenshotsDir, 'step_2_before_menu_click.png');
    await page.screenshot({ path: beforeClickScreenshot });
    screenshots.push(beforeClickScreenshot);

    // Click the mobile menu toggle button
    await page.click('#mobile-menu-toggle');

    // Wait for sidebar overlay or sidebar to appear (sidebar-overlay or .server-sidebar)
    // We'll check for sidebar-overlay to become visible as a sign the menu opened
    await page.waitForSelector('#sidebar-overlay', { state: 'visible', timeout: 3000 });

    // Take a screenshot after opening the menu
    const afterClickScreenshot = path.join(screenshotsDir, 'step_3_after_menu_open.png');
    await page.screenshot({ path: afterClickScreenshot });
    screenshots.push(afterClickScreenshot);

    // Extract data: check if sidebar overlay is visible (menu open)
    const sidebarOverlayVisible = await page.$eval('#sidebar-overlay', el => {
      const style = window.getComputedStyle(el);
      return style && style.display !== 'none' && style.visibility !== 'hidden' && style.opacity !== '0';
    });

    return {
      success: sidebarOverlayVisible,
      action: "Clicked the mobile menu toggle to open the sidebar on mobile layout",
      extractedData: {
        sidebarOverlayVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the mobile menu toggle button using its unique aria-label and fallback to id
    const toggleSelector = 'button#mobile-menu-toggle, button.mobile-menu-toggle, button:has(i.fa-bars)';
    await page.waitForSelector(toggleSelector, { timeout: 4000 });

    // Double-check button is visible and interactable
    const toggleButton = await page.$(toggleSelector);
    if (!toggleButton) throw new Error('Mobile menu toggle button not found');

    // Click the mobile menu toggle button
    await toggleButton.click();

    // Wait for the sidebar overlay to become visible as a sign that the sidebar is open
    // Use both id and class strategies
    await page.waitForSelector('div#sidebar-overlay, .sidebar-overlay', { state: 'visible', timeout: 3000 });

    // Take screenshot after opening menu
    const menuShot = path.join(screenshotsDir, 'step_2_mobile_menu_open.png');
    await page.screenshot({ path: menuShot });
    screenshots.push(menuShot);

    // Verify: check sidebar overlay is now visible
    const overlayVisible = await page.evaluate(() => {
      const el = document.getElementById('sidebar-overlay') || document.querySelector('.sidebar-overlay');
      if (!el) return false;
      const style = window.getComputedStyle(el);
      // Check if displayed and not fully transparent
      return (style && style.display !== 'none' && style.visibility !== 'hidden' && style.opacity !== '0');
    });

    return {
      success: overlayVisible,
      action: "Mobile menu toggle button clicked to open the mobile sidebar menu",
      extractedData: {
        overlayVisible
      },
      screenshots,
      error: overlayVisible ? null : "Sidebar overlay did not become visible after clicking menu toggle"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the mobile menu toggle to be visible (try alternative selector: by aria-label or role if available)
    // Alternative: use button with class and child icon
    await page.waitForSelector('button.mobile-menu-toggle');
    const menuButton = await page.$('button.mobile-menu-toggle');
    if (!menuButton) {
      throw new Error("Mobile menu toggle button not found");
    }

    // Scroll into view if necessary
    await menuButton.scrollIntoViewIfNeeded();

    // Take screenshot before click
    const beforeClickShot = path.join(screenshotsDir, 'step_2_before_menu_click.png');
    await page.screenshot({ path: beforeClickShot });
    screenshots.push(beforeClickShot);

    // Click the menu button
    await menuButton.click();

    // Wait for sidebar overlay to appear (this should indicate the menu opened)
    // Try alternative selector: div with id=sidebar-overlay and check its computed style
    await page.waitForSelector('#sidebar-overlay', { state: 'visible', timeout: 3000 });

    // Take screenshot after click
    const afterClickShot = path.join(screenshotsDir, 'step_3_after_menu_click.png');
    await page.screenshot({ path: afterClickShot });
    screenshots.push(afterClickShot);

    // Verify that the sidebar or overlay is now visible (extract its computed style/display)
    const overlayVisible = await page.evaluate(() => {
      const el = document.getElementById('sidebar-overlay');
      if (!el) return false;
      const style = window.getComputedStyle(el);
      return style.display !== 'none' && style.visibility !== 'hidden' && style.opacity !== '0';
    });

    return {
      success: overlayVisible,
      action: "Clicked the mobile menu toggle and verified the sidebar overlay is visible (mobile menu open)",
      extractedData: {
        sidebarOverlayVisible: overlayVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
