import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Wait for messages list to be loaded
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Find the first message's container using XPath (since structure is dynamic)
    // We'll try to get the first message's "add reaction" button
    // Wait for at least one message to be present
    const messageSelector = '#messages-list > div.message';
    await page.waitForSelector(messageSelector, { timeout: 5000 });

    // Use a different selector strategy: Find the first message's add reaction button by aria-label or emoji icon
    // Try to locate the add reaction button by icon (FontAwesome 'fa-smile' or similar)
    // Get the first message element
    const messageHandles = await page.$$('#messages-list > div.message');
    if (!messageHandles.length) throw new Error('No message elements found');

    // For robustness, look for a button with a smile icon (emoji picker)
    let addReactionButton = null;
    for (const msgHandle of messageHandles) {
      // Search for a button inside the message containing a smile icon
      // Try several likely selectors
      addReactionButton = await msgHandle.$('button[aria-label*="Add Reaction"], button[title*="Add Reaction"], button:has(i.fa-smile), button:has(.fa-smile)');
      if (addReactionButton) break;
      // Try fallback: any button that has an emoji icon
      if (!addReactionButton) {
        addReactionButton = await msgHandle.$('button:has(i[class*="smile"]), button:has(i[class*="emoji"])');
        if (addReactionButton) break;
      }
    }
    if (!addReactionButton) throw new Error('No add reaction button found on any message');

    // Scroll into view and click the add reaction button
    await addReactionButton.scrollIntoViewIfNeeded();
    await addReactionButton.hover();
    await page.waitForTimeout(200); // brief hover for effect
    await addReactionButton.click();

    // Wait for the emoji picker to appear
    // Try to wait for a dialog/modal containing emoji, or a visible emoji picker container
    let emojiPickerSelector = '.emoji-picker, .emoji-picker-modal, [role="dialog"]:has-text("emoji"), [class*="emoji"]';
    await page.waitForTimeout(300); // Give time for picker to animate in
    let pickerFound = false;
    try {
      await page.waitForSelector(emojiPickerSelector, { timeout: 2000 });
      pickerFound = true;
    } catch {
      // Try a fallback: look for any visible modal/dialog with emoji grid
      const emojiDialog = await page.$('[role="dialog"]');
      if (emojiDialog) {
        const dialogText = await emojiDialog.textContent();
        if (dialogText && dialogText.toLowerCase().includes('emoji')) pickerFound = true;
      }
    }

    // Take screenshot after opening emoji picker
    const afterPickerScreenshot = path.join(screenshotsDir, 'step_2_emoji_picker_open.png');
    await page.screenshot({ path: afterPickerScreenshot });
    screenshots.push(afterPickerScreenshot);

    // Extract data for verification: check that emoji picker is present and visible
    const extractedData = {
      emojiPickerOpened: pickerFound
    };

    return {
      success: pickerFound,
      action: pickerFound
        ? "Opened emoji picker for message reaction by clicking add reaction button"
        : "Tried to open emoji picker, but could not verify its presence",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the messages list to be present
    await page.waitForSelector('#messages-list', { timeout: 5000 });

    // Wait for at least one message to be rendered (look for .message or .message-item)
    // Try a generic div with text content of a known message
    const firstMessageSelector = "//div[contains(., 'Hey everyone! Welcome to our Discord server!')]";
    await page.waitForSelector(`xpath=${firstMessageSelector}`, { timeout: 5000 });

    // Hover over the first message to reveal action buttons (simulate Discord-like UI)
    const firstMessage = await page.$(`xpath=${firstMessageSelector}`);
    if (!firstMessage) throw new Error("First message not found");

    await firstMessage.hover();
    await page.waitForTimeout(500); // Wait for any hover effects

    // Try to find the "add reaction" button for the message
    // Look for a button or icon with a smiley/emoji class or title
    // Try by aria-label, title, or icon class
    let reactionButton = null;

    // Try by title attribute
    reactionButton = await firstMessage.$("button[title='Add Reaction']");
    if (!reactionButton) {
      // Try by FontAwesome emoji icon (fa-face-smile or similar)
      reactionButton = await firstMessage.$("i.fa-face-smile, i.fa-smile, i.fa-regular.fa-smile, i.fas.fa-smile");
    }
    if (!reactionButton) {
      // Try any button within the message that is not "Reply" or "More"
      const buttons = await firstMessage.$$('button');
      for (let btn of buttons) {
        const btnText = await btn.textContent();
        const btnTitle = await btn.getAttribute('title');
        if (
          (btnTitle && btnTitle.toLowerCase().includes('reaction')) ||
          (btnText && btnText.includes('ðŸ˜€'))
        ) {
          reactionButton = btn;
          break;
        }
      }
    }
    if (!reactionButton) {
      // Try any element with emoji or reaction in class
      reactionButton = await firstMessage.$("[class*=reaction], [class*=emoji]");
    }
    if (!reactionButton) {
      throw new Error("Add reaction button not found in first message");
    }

    // Click the add reaction button to open the emoji picker
    await reactionButton.click();
    await page.waitForTimeout(500); // Wait for picker to animate in

    // Wait for the emoji picker to appear (try by modal, popover, or emoji grid)
    // Try some common selectors
    let emojiPicker = null;
    const possiblePickers = [
      "div.emoji-picker", // class
      "div[class*=emoji]", // class
      "div[role=dialog]", // ARIA dialog
      "//div[contains(@class,'picker') and contains(.,'ðŸ˜€')]", // XPath
      "//div[contains(@class,'emoji') and contains(@class,'picker')]", // XPath
      "//div[contains(.,'Search Emoji') or contains(.,'Recently Used')]", // Discord-like
