    // If still not found, try to see if any new dialog or popover is present after click
    if (!emojiPicker) {
      // Wait for any new modal/popover/dialog after clicking
      const pickersBefore = await page.$$('div[role=dialog], div.emoji-picker, div[class*=emoji]');
      await page.waitForTimeout(500);
      const pickersAfter = await page.$$('div[role=dialog], div.emoji-picker, div[class*=emoji]');
      if (pickersAfter.length > pickersBefore.length) {
        emojiPicker = pickersAfter[pickersAfter.length-1];
