import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the add server button to be visible
    await page.waitForSelector('.add-server', { timeout: 5000 });

    // Click the add server button to open the server creation modal
    await page.click('.add-server');

    // Wait for the modal to appear (adjust selector as needed)
    await page.waitForSelector('#server-creation-modal', { timeout: 5000 });

    // Take a screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Check if the modal is opened by verifying any expected content
    const modalContent = await page.$eval('#server-creation-modal h2', el => el.textContent);

    return {
      success: true,
      action: "Opened the server creation modal successfully",
      extractedData: { modalContent },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
