    await page.fill('#message-input', message);

    // Take screenshot after typing the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_typed.png'));

    // Send the message by pressing Enter
    await page.keyboard.press('Enter');

    // Wait for a brief moment to ensure the message is sent
    await page.waitForTimeout(1000);

    // Take final screenshot of the message sent
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_message_sent.png'));

    return {
      success: true,
      action: "Successfully navigated to a random channel and sent a message.",
      extractedData: {
        channel: await page.textContent('#current-channel'),
        messageSent: message
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
