import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the mobile menu
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.waitForTimeout(1000); // Wait for the menu to open

    // Take screenshot of the mobile menu opened
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_mobile_menu.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_mobile_menu.png'));

    // Access the member list
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Waiting for member list to be visible

    // Take a screenshot of the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_member_list.png'));

    // Extract member data
    const memberData = await page.evaluate(() => {
      const members = [];
      const memberElements = document.querySelectorAll('.user-details');
      memberElements.forEach(member => {
        const username = member.querySelector('.username')?.textContent || '';
        const status = member.querySelector('.user-status')?.textContent || '';
        members.push({ username, status });
      });
      return members;
    });

    // Take a final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_member_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_member_data.png'));

    return {
      success: true,
      action: "Extracted member data from the server",
      extractedData: { members: memberData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button and click it
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');

    // Wait for and select the member list toggle button
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');

    // Wait for the member list to be visible
    await page.waitForSelector('.member-list', { state: 'visible' });

    // Take screenshot of the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    // Extract member data
    const members = await page.$$eval('.member-item', items => {
      return items.map(item => ({
        username: item.querySelector('.username').innerText,
        avatar: item.querySelector('.user-avatar img').src,
        status: item.querySelector('.user-status').innerText,
      }));
    });

    // Take final screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Extracted member data successfully",
      extractedData: { members },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
