import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button to be visible and click it
    await page.waitForSelector('#mobile-menu-toggle', { timeout: 5000 });
    await page.click('#mobile-menu-toggle');

    // Take screenshot after clicking the mobile menu toggle
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Verify if the sidebar is opened by checking the sidebar overlay
    const isSidebarVisible = await page.isVisible('.sidebar-overlay');

    return {
      success: isSidebarVisible,
      action: isSidebarVisible ? "Opened mobile menu successfully" : "Failed to open mobile menu",
      extractedData: {
        sidebarVisible: isSidebarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
