import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Triple-click the server name to access the admin panel
    const serverNameSelector = '#server-name';
    await page.waitForSelector(serverNameSelector);
    await page.evaluate(selector => {
      const element = document.querySelector(selector);
      const clickEvent = new MouseEvent('click', { bubbles: true });
      element.dispatchEvent(clickEvent);
      element.dispatchEvent(clickEvent);
      element.dispatchEvent(clickEvent);
    }, serverNameSelector);

    // Wait for the 'Export Auth Tokens' button to be visible and click it
    const exportButtonSelector = 'button.export-auth-tokens'; // Update this selector based on actual button
    await page.waitForSelector(exportButtonSelector);
    await page.click(exportButtonSelector);

    // Wait for the download to complete (you may need to adjust this based on your environment)
    await page.waitForTimeout(5000); // Adjust timeout as necessary

    // Take a screenshot after clicking the export button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_export_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_export_clicked.png'));

    // Extract meaningful data to verify task completion
    const extractedData = {
      message: "Export Auth Tokens button clicked successfully."
    };

    return {
      success: true,
      action: "Exported authentication tokens from the admin panel.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

