import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the member list to be visible
    await page.waitForSelector('#toggle-member-list');

    // Click to toggle member list
    await page.click('#toggle-member-list');
    await page.waitForSelector('.user-info');

    // Extract profile information
    const members = await page.$$eval('.user-info', userInfos => {
      return userInfos.map(userInfo => {
        const username = userInfo.querySelector('.username')?.innerText;
        const status = userInfo.querySelector('.user-status')?.innerText;
        return { username, status };
      });
    });

    // Log member information for debugging
    console.log(members);

    // Taking screenshot after extracting member info
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    return {
      success: true,
      action: "Successfully extracted member profile information",
      extractedData: members,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
