import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the server header to be visible
    await page.waitForSelector('.server-header');

    // Click on the server header to open the dropdown menu
    await page.click('.server-header');

    // Wait for the dropdown menu to appear
    await page.waitForSelector('.channel-categories');

    // Take a screenshot after opening the dropdown menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Verify that the dropdown menu is visible
    const isDropdownVisible = await page.isVisible('.channel-categories');

    return {
      success: true,
      action: "Opened the server dropdown menu",
      extractedData: {
        dropdownVisible: isDropdownVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

