import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the toggle button to be visible and click it to show/hide member list
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');

    // Take a screenshot after clicking the toggle button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_toggle_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_toggle_member_list.png'));

    // Verify if the member list is visible or hidden
    const isVisible = await page.isVisible('.member-list'); // Assuming .member-list is the class for the member list
    const actionDescription = isVisible ? "Member list is now visible." : "Member list is now hidden.";

    return {
      success: true,
      action: actionDescription,
      extractedData: { memberListVisible: isVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
