    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the toggle member list button to be visible
    await page.waitForSelector('#toggle-member-list');

    // Click the toggle button to show/hide the member list
    await page.click('#toggle-member-list');

    // Wait for a visual confirmation of the member list visibility change
    await page.waitForTimeout(500); // Adjust timeout if necessary

    // Take a screenshot after toggling the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list_toggled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list_toggled.png'));

    // Verify the action by checking if the member list is displayed
    const memberListVisible = await page.evaluate(() => {
      const memberList = document.querySelector('.member-list'); // Assuming there's a class for member list
      return memberList && memberList.offsetHeight > 0; // Check if it's visible
    });

    return {
      success: true,
      action: memberListVisible ? "Member list is visible" : "Member list is hidden",
      extractedData: { memberListVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the toggle button to be visible
    await page.waitForSelector('#toggle-member-list');

    // Click the toggle button to show/hide the member list
    await page.click('#toggle-member-list');

    // Wait for member list to be visible after toggle action
    await page.waitForSelector('.members-list', { timeout: 5000 });

    // Take a screenshot after toggling the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_toggle.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_toggle.png'));

    // Extract the visibility status of the member list
    const isVisible = await page.$eval('.members-list', el => el.style.display !== 'none');

    return {
      success: true,
      action: "Toggled member list visibility",
      extractedData: { memberListVisible: isVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
