import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first message to be visible
    await page.waitForSelector('.messages-list'); // Adjust selector as necessary
    const firstMessageSelector = '.messages-list > div:first-child'; // Assuming messages are in divs

    // Add thumbs up reaction to the first message
    await page.waitForSelector(firstMessageSelector);
    await page.click(firstMessageSelector); // Click on the first message to open reactions
    await page.waitForTimeout(500); // Wait for the reactions to load

    // Click the thumbs up reaction
    const thumbsUpSelector = 'button[aria-label="Add Reaction"]'; // Adjust selector as necessary
    await page.click(thumbsUpSelector); // Click to open the reaction menu
    await page.waitForSelector('div[aria-label="Thumbs Up"]'); // Wait for thumbs up to be available
    await page.click('div[aria-label="Thumbs Up"]'); // Click thumbs up reaction

    // Take a screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added thumbs up reaction to the first message",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

