import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the settings gear icon to open user settings
    await page.waitForSelector('i.fas.fa-cog'); // Wait for the settings icon to be visible
    await page.click('i.fas.fa-cog'); // Click the settings icon

    // Take screenshot after opening user settings
    await page.waitForSelector('.user-settings-modal'); // Wait for the user settings modal to appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_settings_opened.png'));

    // Verify that the user settings modal is open
    const isModalVisible = await page.isVisible('.user-settings-modal');

    return {
      success: true,
      action: "Opened user settings modal",
      extractedData: { isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

