    return {
      success: true,
      action: "Opened user settings modal",
      extractedData: { modalTitle },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
