import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to a random channel
    const channels = await page.$$('.channel-item');
    const randomChannelIndex = Math.floor(Math.random() * channels.length);
    await channels[randomChannelIndex].click();
    await page.waitForSelector('#current-channel');

    // Take screenshot of the selected channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_selected.png'));

    // Send a specific message
    const messageInput = await page.waitForSelector('#message-input');
    await messageInput.type('Hello, this is a test message!');
    await page.keyboard.press('Enter');

    // Wait for the message to appear in the chat
    await page.waitForTimeout(1000); // Wait for a moment to ensure the message is sent

    // Take screenshot of the sent message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    return {
      success: true,
      action: "Message sent to a random channel",
      extractedData: {
        channel: await page.textContent('#current-channel'),
        message: 'Hello, this is a test message!'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

