import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel sidebar to load
    await page.waitForSelector('.channel-sidebar');

    // Select a random channel from the channel list
    const channelItems = await page.$$('.channel-item');
    const randomIndex = Math.floor(Math.random() * channelItems.length);
    const randomChannel = channelItems[randomIndex];

    // Click on the random channel
    await randomChannel.click();
    await page.waitForTimeout(1000); // Wait for content to load

    // Take a screenshot after navigating to the random channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_random_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_random_channel.png'));

    // Type a message in the input field
    const messageInput = await page.waitForSelector('#message-input');
    await messageInput.type('Hello from the automation script!');

    // Send the message (assuming Enter key sends the message)
    await messageInput.press('Enter');
    await page.waitForTimeout(1000); // Wait for message to be sent

    // Take a screenshot after sending the message
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_message_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_message_sent.png'));

    return {
      success: true,
      action: "Message sent to a random channel",
      extractedData: {
        channel: await randomChannel.evaluate(el => el.innerText),
        message: 'Hello from the automation script!'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
