import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channels to be visible
    await page.waitForSelector('.channel-categories');

    // Click on the "General" voice channel
    await page.click('div.channel-item[data-channel="general-voice"]');
    await page.waitForTimeout(1000); // Wait for the connection to establish

    // Take screenshot after clicking the voice channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_voice_channel_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_voice_channel_clicked.png'));

    // Check for the notification or any change in UI that indicates connection
    const isConnected = await page.evaluate(() => {
      return document.querySelector('.user-controls i.fas.fa-headphones') !== null;
    });

    return {
      success: true,
      action: isConnected ? "Successfully connected to the voice channel and notification triggered." : "Voice channel connection not established.",
      extractedData: { isConnected },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
