import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the announcements channel to be visible and click it
    await page.waitForSelector('.channel-item[data-channel="announcements"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="announcements"]');

    // Wait for the channel to switch and validate
    await page.waitForSelector('#current-channel:has-text("announcements")', { timeout: 5000 });

    // Take a screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_announcements_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_announcements_channel.png'));

    return {
      success: true,
      action: "Switched to announcements channel",
      extractedData: { currentChannel: await page.textContent('#current-channel') },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
