import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button to be visible and click it
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');

    // Take a screenshot after opening the mobile menu
    await page.waitForSelector('.server-sidebar'); // Wait for sidebar to be visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_mobile_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_mobile_menu_opened.png'));

    return {
      success: true,
      action: "Opened the mobile menu",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button to be visible and click it to open the sidebar
    await page.waitForSelector('#mobile-menu-toggle', { state: 'visible' });
    await page.click('#mobile-menu-toggle');

    // Take a screenshot after opening the mobile menu
    await page.waitForTimeout(500); // Give some time for the menu to open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Verify that the sidebar is now visible
    const sidebarVisible = await page.isVisible('.server-sidebar');

    return {
      success: true,
      action: "Opened mobile sidebar menu",
      extractedData: {
        sidebarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button to be visible and click it
    await page.waitForSelector('#mobile-menu-toggle', { timeout: 5000 });
    const mobileMenuToggle = await page.$('#mobile-menu-toggle');
    await mobileMenuToggle.click();

    // Take a screenshot after opening the mobile menu
    await page.waitForSelector('.sidebar-overlay', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_mobile_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_mobile_menu_opened.png'));

    // Verify if the sidebar overlay is visible
    const isSidebarVisible = await page.isVisible('.sidebar-overlay');

    return {
      success: true,
      action: "Mobile menu opened successfully",
      extractedData: { sidebarVisible: isSidebarVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
