import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the voice channel to be visible and click on it
    await page.waitForSelector('.channel-item[data-channel="general-voice"]', { timeout: 5000 });
    await page.click('.channel-item[data-channel="general-voice"]');

    // Wait for the microphone control icon to be visible
    await page.waitForSelector('.user-controls i.fa-microphone', { timeout: 5000 });

    // Click to mute the microphone
    await page.click('.user-controls i.fa-microphone');

    // Take a screenshot after muting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_muted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_muted.png'));

    // Verify if the microphone is muted by checking the icon's class (assuming it changes when muted)
    const isMuted = await page.evaluate(() => {
      const micIcon = document.querySelector('.user-controls i.fa-microphone');
      return micIcon.classList.contains('muted'); // This assumes the muted class is applied
    });

    return {
      success: true,
      action: "Muted the microphone in the voice channel",
      extractedData: { isMuted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
