import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the file input button to be visible and click it
    await page.waitForSelector('#file-input', { state: 'visible' });
    await page.click('#file-input');

    // Upload a file (replace 'path/to/your/file.txt' with the actual file path)
    const filePath = 'path/to/your/file.txt'; // Update this with the actual file path
    const [fileChooser] = await Promise.all([
      page.waitForFileChooser(),
      page.click('#file-input') // This will trigger the file chooser
    ]);
    await fileChooser.setFiles(filePath);

    // Take a screenshot after file upload
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_file_uploaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_file_uploaded.png'));

    // Verify that the file upload was successful (you may need to adjust this based on the UI feedback)
    const uploadSuccess = await page.evaluate(() => {
      const message = document.querySelector('#messages-list'); // Adjust selector as needed
      return message && message.innerText.includes('file uploaded'); // Adjust the condition based on actual feedback
    });

    return {
      success: uploadSuccess,
      action: "File uploaded successfully",
      extractedData: { uploadSuccess },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

