        const timestamp = messageElement.querySelector('.message-timestamp').innerText;
        return { content, timestamp };
      });
    });

    // Take screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_extraction.png'));

    return {
      success: true,
      action: "Collected all message content and timestamps from Discord",
      extractedData: messages,
      screenshots,
