import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the announcements channel to be visible and click it
    await page.waitForSelector('.channel-item[data-channel="announcements"]');
    await page.click('.channel-item[data-channel="announcements"]');

    // Take a screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_switch_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_switch_channel.png'));

    // Verify that the current channel has changed to announcements
    const currentChannel = await page.textContent('#current-channel');
    const extractedData = { currentChannel };

    return {
      success: true,
      action: "Switched to announcements channel",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the channel list to be visible
    await page.waitForSelector('.channel-list');

    // Click on the "announcements" channel
    await page.click('div.channel-item[data-channel="announcements"]');

    // Wait for the announcements channel to be active
    await page.waitForSelector('div.channel-item.active[data-channel="announcements"]');

    // Take a screenshot after switching channels
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_announcements_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_announcements_channel.png'));

    // Extracting the current channel name to verify the switch
    const currentChannel = await page.$eval('#current-channel', el => el.textContent.trim());

    return {
      success: true,
      action: "Switched to the announcements channel successfully.",
      extractedData: { currentChannel },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

