import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the mobile menu toggle to open the sidebar
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.waitForTimeout(1000); // Wait for the menu to appear

    // Take a screenshot after opening the menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Select the 'general' channel
    await page.waitForSelector('.channel-item[data-channel="general"]');
    await page.click('.channel-item[data-channel="general"]');
    await page.waitForTimeout(1000); // Wait for messages to load

    // Take a screenshot of the messages in the 'general' channel
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_general_channel.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_general_channel.png'));

    // Extract messages from the channel
    const messages = await page.$$eval('.messages-list .message', messageElements => {
      return messageElements.map(message => ({
        author: message.querySelector('.author').innerText,
        content: message.querySelector('.content').innerText,
        timestamp: message.querySelector('.timestamp').innerText
      }));
    });

    // Take a final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: true,
      action: "Successfully opened the general channel and extracted messages.",
      extractedData: { messages },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button and click it to open the menu
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');

    // Wait for the member list to be visible and take a screenshot
    await page.waitForSelector('#toggle-member-list');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list_visible.png'));

    // Click to toggle the member list
    await page.click('#toggle-member-list');

    // Wait for the member list to load
    await page.waitForSelector('.user-info');

    // Extract member data
    const members = await page.$$eval('.user-info', memberElements => {
      return memberElements.map(member => {
        const username = member.querySelector('.username')?.textContent || '';
        const status = member.querySelector('.user-status')?.textContent || '';
        return { username, status };
      });
    });

    // Take a screenshot of the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_member_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_member_data.png'));

    // Return success with extracted member data
    return {
      success: true,
      action: "Extracted member data from the Discord-like interface",
      extractedData: { members },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
