import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the announcements channel to be visible and click it
    await page.waitForSelector('div.channel-item[data-channel="announcements"]', { timeout: 5000 });
    await page.click('div.channel-item[data-channel="announcements"]');

    // Take screenshot after switching channels
    await page.waitForTimeout(1000); // Wait for any transitions
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_channel_switched.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_channel_switched.png'));

    // Extract current channel name to verify the switch
    const currentChannel = await page.textContent('#current-channel');

    return {
      success: true,
      action: "Switched to Announcements Channel",
      extractedData: { currentChannel },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

