import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first message to be visible
    await page.waitForSelector('.messages-list'); // Adjust selector if necessary
    const firstMessageSelector = '.messages-list .message:first-child'; // Adjust selector based on actual message structure

    // Wait for the first message to be loaded
    await page.waitForSelector(firstMessageSelector);

    // Click on the reaction button (thumbs up) for the first message
    const reactionButtonSelector = `${firstMessageSelector} .reaction-button`; // Adjust selector based on actual button structure
    await page.click(reactionButtonSelector);

    // Take a screenshot after adding the reaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_reaction_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_reaction_added.png'));

    return {
      success: true,
      action: "Added thumbs up reaction to the first message",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

