import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the mobile menu to open the server list
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.waitForTimeout(500); // wait for the menu to open

    // Triple-click the server name to focus
    const serverName = await page.waitForSelector('#server-name');
    await serverName.click({ clickCount: 3 });
    await page.waitForTimeout(500); // ensure the click is registered

    // Click the 'Export Auth Tokens' button
    const exportButton = await page.waitForSelector('text=Export Auth Tokens');
    await exportButton.click();
    await page.waitForTimeout(2000); // wait for the action to complete

    // Take a screenshot after exporting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_exported.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_exported.png'));

    // Verification of task completion (this may vary based on actual implementation)
    const successMessage = await page.waitForSelector('text=Tokens exported successfully', { timeout: 5000 }).catch(() => null);
    const extractedData = successMessage ? { status: 'Tokens exported' } : { status: 'Export failed' };

    return {
      success: true,
      action: "Exported authentication tokens from the server",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
