    await page.waitForSelector('generate-test-accounts-selector');
    await page.click('generate-test-accounts-selector');

    // Wait for the accounts to be generated
    await page.waitForTimeout(2000); // Adjust timeout as necessary

    // Take a screenshot after generating accounts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_generation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_generation.png'));

    // Extract meaningful data to verify task completion (e.g., number of accounts created)
    const accountsCreated = await page.evaluate(() => {
      // Replace with actual logic to extract the number of accounts created
      return document.querySelector('selector-for-accounts-count').innerText;
    });

