      success: true,
      action: "Generated test accounts successfully",
      extractedData: { successMessage: await successMessage.innerText, accounts: generatedAccounts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
