    screenshots.push(path.join(screenshotsDir, 'step_2_joined_voice_channel.png'));

    return {
      success: true,
      action: "Successfully connected to the General voice channel",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
