export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the mobile menu toggle to access the admin panel
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.waitForTimeout(1000); // Wait for the menu to open

    // Click on the 'Generate Test Accounts' button (assuming it exists)
    // Note: Replace 'generate-test-accounts-button' with the actual selector for the button
    await page.waitForSelector('text=Generate Test Accounts');
    await page.click('text=Generate Test Accounts');
    await page.waitForTimeout(2000); // Wait for accounts to generate

    // Take a screenshot after generating accounts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_generation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_generation.png'));

    // Extract meaningful data to verify task completion
    const generatedAccounts = await page.evaluate(() => {
      // Assuming there's a way to get the list of generated accounts
      return Array.from(document.querySelectorAll('.account-item')).map(account => account.textContent);
    });

    return {
      success: true,
      action: "Generated fake user accounts successfully",
      extractedData: { generatedAccounts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

