import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the mobile menu toggle to open the admin options
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');

    // Wait for the 'Generate Test Accounts' button to be visible and click it
    await page.waitForSelector('button:has-text("Generate Test Accounts")', { timeout: 5000 });
    await page.click('button:has-text("Generate Test Accounts")');

    // Wait for a confirmation message or the new accounts to appear
    await page.waitForSelector('.account-generated-message', { timeout: 5000 });

    // Take a screenshot after generating accounts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_generating_accounts.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_generating_accounts.png'));

    return {
      success: true,
      action: "Generated test accounts successfully",
      extractedData: {
        message: await page.innerText('.account-generated-message'),
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
