import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the message to add a reaction
    await page.waitForSelector('.messages-list'); // Wait for messages to load
    const messageSelector = 'div.messages-list div.channel-item:nth-child(1)'; // Adjust this selector based on the message you want to react to
    await page.click(messageSelector); // Click on the first message

    // Wait for the add reaction button to appear
    await page.waitForSelector('button.add-reaction'); // Adjust selector if necessary
    await page.click('button.add-reaction'); // Click the add reaction button

    // Wait for the emoji picker to open
    await page.waitForSelector('.emoji-picker'); // Adjust selector based on the actual emoji picker class
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_opened.png'));

    // Optionally, select an emoji (this part can be customized)
    await page.click('.emoji-picker .emoji:nth-child(1)'); // Click on the first emoji

    return {
      success: true,
      action: "Opened emoji picker for message reaction",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

