import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the settings gear icon to open user settings
    await page.waitForSelector('.user-controls .fa-cog'); // Wait for the settings icon to be visible
    await page.click('.user-controls .fa-cog'); // Click the settings icon
    await page.waitForLoadState('networkidle'); // Wait for any potential loading

    // Take screenshot after opening user settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_settings_opened.png'));

    return {
      success: true,
      action: "User settings modal opened successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

