import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu toggle button and click it
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Wait for the server sidebar and select the server
    await page.waitForSelector('.server-item[data-server="home"]');
    await page.click('.server-item[data-server="home"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_server_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_server_selected.png'));

    // Wait for the channel and select the general channel
    await page.waitForSelector('.channel-item[data-channel="general"]');
    await page.click('.channel-item[data-channel="general"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_channel_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_channel_selected.png'));

    // Extract member data (mock up as needed)
    const memberListVisible = await page.$('#toggle-member-list');
    if (memberListVisible) {
      await page.click('#toggle-member-list');
      await page.waitForSelector('.user-info');
      const members = await page.evaluate(() => {
        return Array.from(document.querySelectorAll('.user-details')).map(user => ({
          username: user.querySelector('.username').innerText,
          status: user.querySelector('.user-status').innerText,
        }));
      });

      await page.screenshot({ path: path.join(screenshotsDir, 'step_5_members_extracted.png') });
      screenshots.push(path.join(screenshotsDir, 'step_5_members_extracted.png'));

      return {
        success: true,
        action: "Extracted member data successfully",
        extractedData: members,
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "Member list not visible",
        extractedData: {},
        screenshots,
        error: "Member list could not be displayed."
      };
    }

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Access the admin panel
    await page.waitForSelector('#mobile-menu-toggle');
    await page.click('#mobile-menu-toggle');
    await page.waitForTimeout(1000); // Wait for the menu to open

    // Access the member list
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Wait for the member list to load

    // Extract member data
    const members = await page.evaluate(() => {
      const memberElements = document.querySelectorAll('.user-info');
      return Array.from(memberElements).map(member => ({
        username: member.querySelector('.username') ? member.querySelector('.username').innerText : null,
        status: member.querySelector('.user-status') ? member.querySelector('.user-status').innerText : null,
      }));
    });

    // Take screenshot of the members list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    return {
      success: true,
      action: "Successfully accessed the member list and extracted data",
      extractedData: { members },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
