import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Triple-click on the server name to access the admin panel
    const serverNameSelector = '#server-name';
    await page.waitForSelector(serverNameSelector);
    await page.click(serverNameSelector, { clickCount: 3 });

    // Wait for the 'Export Auth Tokens' button to appear and click it
    const exportButtonSelector = 'button#export-auth-tokens'; // Assuming this is the selector for the button
    await page.waitForSelector(exportButtonSelector);
    await page.click(exportButtonSelector);

    // Wait for the download to complete (this may require additional logic based on your setup)
    await page.waitForTimeout(2000); // Adjust timeout as necessary

    // Take a screenshot after the action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_export.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_export.png'));

    return {
      success: true,
      action: "Exported authentication tokens successfully",
      extractedData: {}, // You can add logic to extract data if needed
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

