import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search icon to be visible and click it to open the search modal
    await page.waitForSelector('i.fas.fa-search');
    await page.click('i.fas.fa-search');

    // Take a screenshot after opening the search modal
    await page.waitForSelector('#search-input'); // Wait for the search input to be visible
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_modal_opened.png'));

    // Verify that the search input is present
    const isSearchInputVisible = await page.isVisible('#search-input');

    return {
      success: true,
      action: "Opened the search modal successfully",
      extractedData: { isSearchInputVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

