import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open search modal
    await page.click('#mobile-menu-toggle');
    await page.waitForSelector('#search-input');

    // Perform empty search to retrieve all messages
    await page.fill('#search-input', '');
    await page.keyboard.press('Enter');
    await page.waitForTimeout(1000); // Wait for messages to load

    // Extract message content and timestamps
    const messages = await page.$$eval('.messages-list .message', messageElements => {
      return messageElements.map(message => {
        const content = message.querySelector('.message-content').innerText;
        const timestamp = message.querySelector('.timestamp').innerText;
        return { content, timestamp };
      });
    });

    // Take screenshot after messages are loaded
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_messages_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_messages_loaded.png'));

    return {
      success: true,
      action: "Collected all message content and timestamps",
      extractedData: messages,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

