    return {
      success: true,
      action: "Collected message content and timestamps",
      extractedData: { messages },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
