import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the add server button to be visible and click it
    await page.waitForSelector('.add-server', { state: 'visible' });
    await page.click('.add-server');

    // Take a screenshot after opening the server creation modal
    await page.waitForSelector('.server-creation-modal', { state: 'visible' }); // Assuming the modal has a class
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_server_creation_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_server_creation_modal.png'));

    // Extract meaningful data to verify task completion
    const modalVisible = await page.isVisible('.server-creation-modal');

    return {
      success: true,
      action: "Opened server creation modal",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

