import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the member list
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Wait for the member list to open

    // Take a screenshot of the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    // Extract member data (this would be specific to the structure of the member list)
    const members = await page.evaluate(() => {
      const memberElements = document.querySelectorAll('.user-details');
      return Array.from(memberElements).map(member => {
        const username = member.querySelector('.username').innerText;
        const status = member.querySelector('.user-status').innerText;
        return { username, status };
      });
    });

    // Take a screenshot of the extracted data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    // Return the extracted data and success message
    return {
      success: true,
      action: "Extracted member data successfully",
      extractedData: { members },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Access the member list by waiting for the toggle button and clicking it
    await page.waitForSelector('#toggle-member-list');
    await page.click('#toggle-member-list');
    await page.waitForTimeout(1000); // Wait for member list to load

    // Take a screenshot of the member list
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_member_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_member_list.png'));

    // Extract member data
    const members = await page.evaluate(() => {
      const memberElements = document.querySelectorAll('.user-info');
      return Array.from(memberElements).map(member => {
        const username = member.querySelector('.username')?.innerText || '';
        const status = member.querySelector('.user-status')?.innerText || '';
        return { username, status };
      });
    });

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    return {
      success: true,
      action: "Successfully accessed the member list and extracted member data",
      extractedData: { members },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
